import { Component, OnInit } from '@angular/core';

interface IProduct {
  pId: string,
  name: string,
  imgage: string,
  price: number,
  qty: number

}



@Component({
  selector: 'app-ShoppingCart',
  templateUrl: './ShoppingCart.component.html',
  styleUrls: ['./ShoppingCart.component.scss']
})
export class ShoppingCartComponent implements OnInit {

  public products: IProduct[] = [
    {
      pId: "POO1",
      name: "Sam Sung A50",
      imgage: "data:image/jpeg;base64,",
      price: 1400000,
      qty: 13
    },
    {
      pId: "POO2",
      name: "nokia A50",
      imgage: "data:image/jpeg;base64,",
      price: 1400000,
      qty: 41
    },

    {
      pId: "POO3",
      name: "Iphone A50",
      imgage: "data:image/jpeg;base64,",
      price: 1400000,
      qty: 7
    },
    {
      pId: "POO4",
      name: "Xiao mi red 10",
      imgage: "data:image/jpeg;base64,",
      price: 1400000,
      qty: 10
    }
  ];




  constructor() { }

  ngOnInit() {
  }
  public onHandleChangeQty(id: string, qty: number): void {
    this.products = this.products.map((product: IProduct) => {
      if (product.pId == id) {
        return {
          ...product,
          qty: product.qty + qty
        }
      }
      // console.log(product);
      return product;
    })
    // console.log(this.products);
  }
}
